import fetch from 'isomorphic-unfetch';
import {useState} from 'react';
import css from './resetpassword.module.scss';


const ResetPassword = props => {
   
    const [email, setEmail] = useState();
    const [message, setMessage] = useState();

    function handleSubmit(event) {
      event.preventDefault();
      fetch('http://localhost:4000/resetpassword', { email })
        .then(resp => resp.ok !== false && redirectTo('/'));
    }
  
    return (
    <div className={css.resetpassword}>
        <h2>Forget password</h2>
        <form onSubmit={handleSubmit}>
          <p>Do not worry. Simply enter your email address below.</p>
          <label htmlFor="email">
            <input
              id="email"
              type="email"
              placeholder="Email"
              value={email}
              onChange={e => setEmail(e.target.value)}
            />
          </label>
          <br />
          <button type="submit">Submit</button>
        </form>
        </div>
    );
};

export default ResetPassword;
