import css from './admin.module.scss';
import {useEffect, useState} from "react";
import Link from 'next/link';

const Admin = ()=>{

    const [products, setProducts] = useState(["o","p"]);

    useEffect(()=>{
getProducts();
    }, []);


    const getProducts = async function() {
        try{
      const res = await fetch('http://localhost:4000/products');
          const data = await res.json();
         setProducts(data.data);
        }catch{
      console.log("Unable to load Products");
     setProducts("")
        }
        };

       const addButton = ()=>{
         document.getElementsByClassName("show_from").style.display = "block";
       }

    return(
        <div className={css.admin}>
<h1 className={css.admin_header}>Admin Page</h1>
            <div className={css.add_product}>
                <button onClick={addButton}>Add product</button>
                <div>
                <form className={css.show} action="/action_page.php">
    <label for="fname">First Name</label>
    <input type="text" id="fname" name="firstname" placeholder="Your name.." />

    <label for="lname">Last Name</label>
    <input type="text" id="lname" name="lastname" placeholder="Your last name.." />

    <label for="country">Country</label>
    <select id="country" name="country">
      <option value="australia">Australia</option>
      <option value="canada">Canada</option>
      <option value="usa">USA</option>
    </select>
  
    <input type="submit" value="Submit" />
  </form>
                </div>
            </div>
 
 <table className={css.table}>
        <thead>
          <tr>
            <th >
              <div >Product</div>
            </th>
            <th >
              <div >Price</div>
            </th>
            <th >
              <div >In stock</div>
            </th>
            <th >
              <div >Action</div>
            </th>
          </tr>
        </thead>
        <tbody>
          {
          products.map((item) => (
            <tr key={item.id}>
              <td >
                <div>
                  <img
                    src={item.image}
                    alt=""
                    width="70"
                  />
                  <br />
                  <Link href="/products/[id]" as={`/products/${item.id}`}>
<a> {item.name}</a></Link>            
                </div>

              </td>
              <td >
                <strong>â‚¦ {item.price}</strong>
              </td>
              <td>
                {item.quantity}
                </td>
                <td>
                <button>Edit</button>
                <button>Delete</button>
                </td>
            </tr>
           
          ))}
        </tbody>
      </table>
        </div>
    )
}




export default Admin;
