import React, { useState } from 'react';
import css from './account.module.scss';
import axios from 'axios';

const Account = (props)=>{
    const [user, setUser] = useState([{email:'', password:''}]);

const handleChange = (e)=>{
     let input =[...user];
     input.push({[e.target.name]:e.target.value
     });
setUser(input);
}


const handleSubmit = (e)=>{
    e.preventDefault();
console.log(user);
}

    return(
        <div className={css.account}>
<h1 className={css.heading}>Account</h1>

<div className={css.form}>
<form className={css.signup}>
 <fieldset>
  <legend>Create Account:</legend>
  <label for="fname">First name:</label> <br />
  < input type="text" id="fname" name="fname" placeholder="First Name" required  onChange={handleChange}/>
  <br />
  <label for="lname">Last name:</label> <br />
  <input type="text" id="lname" name="lname" placeholder="Last Name" required onChange={handleChange}/>
  <br />
  <label for="email">Email:</label> <br />
  <input type="email" id="email" name="email" placeholder="johndoe@gmail.com" required onChange={handleChange}/>  
  <br />
  <label for="number">phone number:</label> <br/>
  <input type="number" id="number" name="number" placeholder="+2348090213567" required onChange={handleChange}/>
  <br />
  <label for="address">phone number:</label> <br />
  <input type="text" id="address" name="address" placeholder="4, johnson Street, balogun, Lagos" required onChange={handleChange}/>
  <br />
  <label for="password">password:</label> <br />
  <input type="password" id="password" name="passweord" required onChange={handleChange}/>  
  <br />
  <button onClick={handleSubmit}>Submit</button>
 </fieldset>
</form>
<form className={css.login}>
 <fieldset>
  <legend>Login:</legend>
  <label for="email">Email:</label> <br />
  <input type="email" id="email" name="email" onChange={handleChange}/>  
  <br />
  <label for="password">password:</label> <br />
  <input type="password" id="password" name="passweord" onChange={handleChange}/>  
  <button onClick={handleSubmit}>Submit</button>
 </fieldset>
</form>
</div>
</div>

    );
}

export default Account;
