import {useState} from 'react';
import css from './resetpassword.module.scss';
import axios from "axios";

const ResetPassword = props => {
   
    const [email, setEmail] = useState();
    const [message, setMessage] = useState();

   const  handleSubmit = async (event) =>{
      event.preventDefault();
      const response = await axios.post("http://localhost:4000/resetpassword", {"email":email}, {
        timeout: 30000
      });
console.log(response);


    }
  
    return (
    <div className={css.resetpassword}>
        <h2>Forget password</h2>
        <form onSubmit={handleSubmit}>
          <p>Do not worry. Simply enter your email address below.</p>
          <label htmlFor="email">
            <input
              id="email"
              type="email"
              placeholder="Email"
              value={email}
              onChange={e => setEmail(e.target.value)}
            />
          </label>
          <br />
          <button type="submit">Submit</button>
        </form>
        </div>
    );
};

export default ResetPassword;
