import css from './admin.module.scss';
import {useEffect, useState} from "react";
import Link from 'next/link';
import axios from 'axios';
import Loading from '../components/loading';

const Admin = ()=>{

    const [products, setProducts] = useState(["o","p"]);
    const [loading, setLoading] = useState(false);
    const [name, setName] = useState();
    const [price, setPrice] = useState();
    const [category, setCategory] = useState();
    const [image, setImage] = useState();

    useEffect(()=>{
getProducts();
    }, []);


    const getProducts = async function() {
        try{
      const res = await fetch('http://localhost:4000/products');
          const data = await res.json();
         setProducts(data.data);
        }catch{
      console.log("Unable to load Products");
     setProducts("")
        }
        };

       const addButton = ()=>{
        let elem = document.getElementsByClassName("admin_form")[0];
        elem.style.display === "block"?elem.style.display = "none":elem.style.display = "block";
       }


       //get form values
       const handleChange = (e) =>{
         console.log(e.target.name)
switch(e.target.name){
  case "name":
    setName(e.target.value);
      break;
    case "price":
      setPrice(e.target.value);
    break;
    case "category":
      setCategory(e.target.value);
      break;
   default: setImage(e.target.value);
 
       }
       console.log(name);
      }

      
        //submit form data to backend server
        const handleSubmit =async (e) => {
          setLoading(true);
         e.preventDefault();
         
      const url = 'http://localhost:4000/prescription';
        console.log(state);  
        try{
        const response = await axios.post(url, state, {
          timeout: 30000
        });
        if(response.status==200){
         console.log(response);
         setLoading(false);
        }
      }
        catch(error){
          console.log(error);
          setLoading(false);
        }
        
        };
      


if(loading){
  return <Loading />
}

    return(
        <div className={css.admin}>
<h1 className={css.admin_header}>Admin Page</h1>
            <div className={css.add_product}>
              {/** Handle show upload form */}
                <button onClick={addButton}>Add product</button>
                <div>
                <form className={css.show} className="admin_form">
    <label for="name">Name</label>
    <input type="text" id="name" name="name" placeholder="product name" value={name} onChange={handleChange}/>

    <label for="description">Description</label>
    <textarea type="text" id="description" name="description" placeholder="Product Description" />

    <label for="category">category</label>
    <select id="category" name="category">
      <option value="antibiotics">Antibiotics</option>
      <option value="antidiabetics">Antidiabetics</option>
      <option value="antimaleria">Antimaleria</option>
    </select>
    <button onClick={handleSubmit}>Submit</button>
    <br />
    <label for="image">Upload image</label>
    <input type="file" id="image" name="image" placeholder="Select Image" />
  </form>
                </div>
            </div>
 
 <table className={css.table}>
        <thead>
          <tr>
            <th >
              <div >Product</div>
            </th>
            <th >
              <div >Category</div>
            </th>
            <th >
              <div >Price</div>
            </th>
            <th >
              <div >In stock</div>
            </th>
            <th >
              <div >Action</div>
            </th>
          </tr>
        </thead>
        <tbody>
          {
          products.map((item) => (
            <tr key={item.id}>
              <td >
                <div>
                  <img
                    src={item.image}
                    alt=""
                    width="70"
                  />
                  <br />
                  <Link href="/products/[id]" as={`/products/${item.id}`}>
<a> {item.name}</a></Link>            
                </div>

              </td>
              <td >
                <strong>{item.category}</strong>
              </td>
              <td >
                <strong>₦ {item.price}</strong>
              </td>
              <td>
                {item.quantity}
                </td>
                <td>
                <button>Edit</button>
                <button>Delete</button>
                </td>
            </tr>
           
          ))}
        </tbody>
      </table>
        </div>
    )
}




export default Admin;
