import css from './admin.module.scss';

const Admin = (props)=>{

    return(
        <div className={css.admin}>
 
 <table className={css.table}>
        <thead>
          <tr>
            <th >
              <div >Product</div>
            </th>
            <th >
              <div >Price</div>
            </th>
            <th >
              <div >In stock</div>
            </th>
          </tr>
        </thead>
        <tbody>
          {props.products.map((item) => (
            <tr key={item.id}>
              <td >
                <div>
                  <img
                    src={item.image}
                    alt=""
                    width="70"
                  />
                  <br />
                  <Link href="/products/[id]" as={`/products/${item.id}`}>
<a> {item.name}</a></Link>            
                </div>

              </td>
              <td >
                <strong>â‚¦ {item.price}</strong>
              </td>
              <td>
                {item.quantity}
                </td>
            </tr>
           
          ))}
        </tbody>
      </table>
        </div>
    )
}


Admin.getInitialProps = async function() {
    try{
  const res = await fetch('http://localhost:4000/products');
      const data = await res.json();
      console.log(data);
      return {
        products: data.data
      };
    }catch{
  console.log("Unable to load Products");
  return {
    products:[]
  };
    }
    
    
  
  
    };

export default Admin;
