import css from './admin.module.scss';
import {useEffect, useState} from "react";
import Link from 'next/link';

const Admin = ()=>{

    const [products, setProducts] = useState(["o","p"]);

    useEffect(()=>{
getProducts();
    }, []);


    const getProducts = async function() {
        try{
      const res = await fetch('http://localhost:4000/products');
          const data = await res.json();
         setProducts(data.data);
        }catch{
      console.log("Unable to load Products");
     setProducts("")
        }
        };

       const addButton = ()=>{
        let elem = document.getElementsByClassName("admin_form")[0];
        elem.style.display = "block";
       }


       const submitProduct =(e)=>{
         e.preventDefault();
console.log("submit");
       }

    return(
        <div className={css.admin}>
<h1 className={css.admin_header}>Admin Page</h1>
            <div className={css.add_product}>
                <button onClick={addButton}>Add product</button>
                <div>
                <form className={css.show} className="admin_form">
    <label for="name">Name</label>
    <input type="text" id="name" name="name" placeholder="product name" />

    <label for="description">Description</label>
    <textarea type="text" id="description" name="description" placeholder="Product Description" />

    <label for="category">category</label>
    <select id="category" name="category">
      <option value="antibiotics">Antibiotics</option>
      <option value="antidiabetics">Antidiabetics</option>
      <option value="antimaleria">Antimaleria</option>
    </select>
  
    <button onClick={submitProduct}>Submit</button>
  </form>
                </div>
            </div>
 
 <table className={css.table}>
        <thead>
          <tr>
            <th >
              <div >Product</div>
            </th>
            <th >
              <div >Category</div>
            </th>
            <th >
              <div >Price</div>
            </th>
            <th >
              <div >In stock</div>
            </th>
            <th >
              <div >Action</div>
            </th>
          </tr>
        </thead>
        <tbody>
          {
          products.map((item) => (
            <tr key={item.id}>
              <td >
                <div>
                  <img
                    src={item.image}
                    alt=""
                    width="70"
                  />
                  <br />
                  <Link href="/products/[id]" as={`/products/${item.id}`}>
<a> {item.name}</a></Link>            
                </div>

              </td>
              <td >
                <strong>{item.category}</strong>
              </td>
              <td >
                <strong>â‚¦ {item.price}</strong>
              </td>
              <td>
                {item.quantity}
                </td>
                <td>
                <button>Edit</button>
                <button>Delete</button>
                </td>
            </tr>
           
          ))}
        </tbody>
      </table>
        </div>
    )
}




export default Admin;
