import {useState, useEffect} from "react";
import css from './standingorder.module.scss';


const StandingOrder = () =>{
const [data, setData] = useState("");
useEffect( ()=>{
fetchData();

}, [])


const fetchData =async ()=>{
  const res = await fetch("http://localhost:4000/standingorders/1", {
      method:"GET",
      headers: {
        'Content-Type': 'application/json'
      },
  });
  const data = await res.json();
  console.log(data)
  setData(data);
}

if(data.data){
 return(
    <div className={css.standingorder}>
        <ul>
          {data.data.map((item)=>{
              return <li><div className={css.product}>{item.productId}</div><div className={css.orderdate}>{item.date_created}</div></li>
          })}
        </ul>
    </div>
)   
}
return <div className={css.standingorder}>{data.message}</div>;

}

export default StandingOrder;
