import {useState} from 'react';
import axios from "axios";

const ResetPassword = ({ valid, token }) => {
  
  
  const [password, setPassword] = useState();


  const  handleSubmit = async (event) =>{
    event.preventDefault();
    const response = await axios.post("http://localhost:4000/resetpassword", {"password":password}, {
      timeout: 30000
    });
    console.log(response);
  }


    return (
    <div>
      <br />
      <br />
        <h2 style={style}>Forget password</h2>
        <br />
        {valid ? (
          <>
            <p style={style}>Enter your new password.</p>
            <br />
            <form onSubmit={handleSubmit}>
              <div>
                <input
                  type="password"
                  placeholder="New password"
                  value={password}
                  onChange={e => setPassword(e.target.value)}
                />
              </div>
              <button type="submit">Set new password</button>
            </form>
          </>
        ) : (
          <p>This link may have been expired</p>
        )}
     </div>
    );
  };

  const style = {
    "margin":"auto", 
    "text-align":"center"
  }
  
  ResetPassword.getInitialProps = async ctx => {
    const { token } = ctx.query;
    const valid = await fetch(
      `http://localhost:4000/confirmation/${token}`,
      { method: 'POST' }
    )
      console.log(valid);
    return { token};

  };
  
  export default ResetPassword;
